import React, { useState } from 'react';
import {useEffect} from 'react';
import axios from 'axios';
const Table =()=>{
    const [name,setName] = useState("");
    const [tcases,setTcases] = useState("");
    const [acases,setAcases] = useState("");
    const [trec,setTrec] = useState("");
    const [tdea,setTdea] = useState("");

    useEffect(()=>{
        async function getData(){
            const poke = await axios.get(`https://api.covid19india.org/data.json`); 
            const data = poke.data.statewise;
            console.log(data);
            const ndata = data.map((values)=>{
                setName(values.state);

            })
        }
        getData();
    });
    return(<>
        <table className="table ">
            <thead className="table-dark">
                <tr>
                    <td className ="text-center">State</td>
                    <td className ="text-center">Total Cases</td>
                    <td className ="text-center">Active Cases</td>
                    <td className ="text-center">Total Recovered</td>
                    <td className ="text-center">Total Deaths</td>
                </tr>
            </thead>
            <tbody className ="t-rows">
            <tr>
                    <td className ="text-center">Country</td>
                    <td className ="text-center">Total Cases</td>
                    <td className ="text-center">Active Cases</td>
                    <td className ="text-center">Total Recovered</td>
                    <td className ="text-center">Total Deaths</td>
                </tr>
            </tbody>
        </table>
    
    </>);
}

export default Table;
